def find_party(N: int, friendships: List[Tuple[int, int]]) -> Optional[List[int]]:
    # Create a graph representation using adjacency list
    graph = {i: [] for i in range(1, N + 1)}
    for u, v in friendships:
        graph[u].append(v)
        graph[v].append(u)

    # Function to check if a group of 5 people are all friends or no friends
    def is_valid_party(group):
        for i in range(5):
            for j in range(i + 1, 5):
                if group[i] in graph[group[j]]:
                    return False
        return True

    # Try to find a valid party of 5 people
    for a in range(1, N + 1):
        for b in graph[a]:
            for c in graph[b]:
                if c != a:
                    for d in graph[c]:
                        if d not in (a, b):
                            for e in graph[d]:
                                if e not in (a, b, c) and e != d:
                                    party = [a, b, c, d, e]
                                    if is_valid_party(party):
                                        return party
    return None

