from typing import List, Optional, Tuple

def find_weight_order(N: int, weights: List[int], S: str) -> Optional[List[Tuple[int, str]]]:
    if N == 1:
        if S[0] == 'L':
            return [(weights[0], 'L')]
        else:
            return [(weights[0], 'R')]

    # Create a list of tuples (weight, index, side)
    weight_index_side = [(weights[i], i, S[i]) for i in range(N)]

    # Sort the list by weight
    weight_index_side.sort(key=lambda x: x[0])

    # Initialize the result list
    result = []

    # Initialize the left and right sums
    left_sum = 0
    right_sum = sum(weights)

    # Iterate over the sorted list
    for weight, index, side in weight_index_side:
        if side == 'L':
            if left_sum + weight < right_sum - weight:
                result.append((weight, 'L'))
                left_sum += weight
                right_sum -= weight
            else:
                return None
        else:
            if right_sum + weight > left_sum:
                result.append((weight, 'R'))
                right_sum += weight
                left_sum -= weight
            else:
                return None

    return result

