from typing import List

def find_permutation(n: int) -> List[int]:
    permutation = [0] * n

    def query(a: List[int]) -> int:
        s = [p + a[i] for i, p in enumerate(permutation)]
        freq = {}
        for num in s:
            if num in freq:
                return s.index(num) + 1
            freq[num] = 1
        return 0

    for i in range(n):
        a = [0] * n
        a[i] = 1
        k = query(a)
        if k != 0:
            permutation[i] = k - 1

    return permutation

