from collections import deque

def func(n: int, a: list[int]) -> int:
    deq = deque()
    inversions = 0

    for num in a:
        if not deq or deq[0] > num:
            deq.appendleft(num)
        else:
            deq.append(num)
        # Count inversions
        for i in range(len(deq) - 1):
            if deq[i] > deq[i + 1]:
                inversions += 1

    return inversions

