from typing import List

def calculate_lis(subsequence: List[int]) -> int:
    if not subsequence:
        return 0
    dp = [1] * len(subsequence)
    for i in range(1, len(subsequence)):
        for j in range(i):
            if subsequence[i] > subsequence[j]:
                dp[i] = max(dp[i], dp[j] + 1)
    return max(dp)

def can_split_sequence(n: int, sequence: List[int]) -> str:
    # Check if the sum of the sequence is even, if not, it's impossible to split it such that the XOR of the LIS lengths is 0
    total_xor = 0
    for num in sequence:
        total_xor ^= num

    if total_xor % 2 == 1:
        return "NO"

    # If the total XOR is even, it is possible to split the sequence
    return "YES"

