def find_simple_segments(n: int, a: list, b: list) -> int:
    def is_simple_segment(l, r):
        x = 1
        for i in range(l - 1, r):
            if b[i] == '*':
                x *= a[i]
            elif b[i] == '/':
                if a[i] == 0:
                    return False
                x = x / a[i]
            if x != int(x):
                return False
        return True

    count = 0
    for l in range(n):
        for r in range(l, n):
            if is_simple_segment(l + 1, r + 1):
                count += 1
    return count

