def gcd(a, b):
    while b:
        a, b = b, a % b
    return a

def c(l, r):
    count = 0
    for i in range(l, r + 1):
        for j in range(i, r + 1, i):
            if gcd(i, j) >= l:
                count += 1
    return count

def func(n, k):
