    for i in range(n - k + 1):
        x = [j for j in range(i, i + k + 1)]
        x.append(n)
        current_sum = 0
        for j in range(k):
            current_sum += c(x[j] + 1, x[j + 1])
        min_sum = min(min_sum, current_sum)
    return min_sum

