def update_illusion_rate(graph: Dict[int, List[Tuple[int, int]]], room: int, new_rate: int) -> None:
    for neighbor, _ in graph[room]:
        if neighbor == room:  # Assuming the graph is undirected and the tuple is (neighbor, rate)
            graph[neighbor][graph[neighbor].index((room, graph[neighbor][0]))] = (room, new_rate)

def find_min_energy(graph: Dict[int, List[Tuple[int, int]]], start: int, end: int, a: List[int]) -> int:
    pq = [(0, start)]
    visited = set()
    while pq:
        energy, room = heapq.heappop(pq)
        if room == end:
            return energy
        if room in visited:
            continue
        visited.add(room)
        for neighbor, rate in graph[room]:
            new_energy = max(abs(rate + a[neighbor - 1]), abs(rate - a[neighbor - 1]))
            heapq.heappush(pq, (energy + new_energy, neighbor))
    return -1  # If no path is found

