from typing import List, Tuple

def process_queries(n: int, m: int, q: int, queries: List[Tuple[int, int]]) -> List[int]:
    # Initialize the matrix with all cells free
    matrix = [[True] * m for _ in range(n)]
    results = []

    def is_staircase(x1, y1, x2, y2, x3, y3, x4, y4):
        if not (0 <= x1 < n and 0 <= y1 < m and 0 <= x2 < n and 0 <= y2 < m and 0 <= x3 < n and 0 <= y3 < m and 0 <= x4 < n and 0 <= y4 < m):
            return False
        if not (matrix[x1][y1] and matrix[x2][y2] and matrix[x3][y3] and matrix[x4][y4]):
            return False
        return True

    def count_staircases():
        count = 0
        for i in range(n):
            for j in range(m):
                if matrix[i][j]:
                    # Check for both types of staircases
                    if is_staircase(i, j, i + 1, j + 1, i + 2, j + 2, i + 3, j + 3):
                        count += 1
                    if is_staircase(i, j, i + 1, j - 1, i + 2, j - 2, i + 3, j - 3):
                        count += 1
        return count

    current_count = count_staircases()
    results.append(current_count)

    for x, y in queries:
        # Flip the state of the cell
        matrix[x - 1][y - 1] = not matrix[x - 1][y - 1]
        # Recount the staircases
        current_count = count_staircases()
        results.append(current_count)

    return results

