MOD = 998244353

def count_sequences(n: int, k: int, x: int) -> int:
    if x == 0:
        # If x is 0, we need to count sequences where no subsequence has XOR equal to 0
        # This is equivalent to counting sequences where all subsequences have XOR not equal to 0
        # This can be computed using the inclusion-exclusion principle
        total_sequences = pow(2, n * k, MOD)
        no_zero_subsequence = pow(2, n * k - k, MOD)
        return (total_sequences - no_zero_subsequence) % MOD
    else:
        # If x is not 0, we can use a simpler approach
        # We need to count sequences where no subsequence has XOR equal to x
        # This can be computed using dynamic programming
        max_subsequence_length = min(20, k)
        dp = [[0] * (1 << max_subsequence_length) for _ in range(max_subsequence_length + 1)]
        dp[0][0] = 1

        for length in range(1, max_subsequence_length + 1):
            for mask in range(1 << length):
                for prev_mask in range(1 << (length - 1)):
                    if (mask & prev_mask) == 0 and (mask | prev_mask) != (1 << length) - 1:
                        dp[length][mask] = (dp[length][mask] + dp[length - 1][prev_mask]) % MOD

        result = 0
        for mask in range(1 << k):
            if (mask ^ x) != 0:
                count = 0
                for length in range(1, max_subsequence_length + 1):
                    if (mask & ((1 << length) - 1)) == 0:
                        count = (count + dp[length][mask & ((1 << length) - 1)]) % MOD
                result = (result + pow(count, n, MOD)) % MOD

        return result

