def func(n: int, bubbles: List[Tuple[int, int]], q: int, finalists: List[Tuple[int, int, int]]) -> List[int]:
    def dot_product(dx, dy, x, y):
        return dx * x + dy * y

    def cross_product(dx, dy, x, y):
        return dx * y - dy * x

    def angle_between(dx, dy, x, y):
        return math.atan2(dy, dx) - math.atan2(y, x)

    def get_next_bubble(dx, dy, x, y):
        min_angle = float('inf')
        next_bubble = None
        for i in range(n):
            bx, by = bubbles[i]
            if (bx, by) != (x, y):
                angle = angle_between(dx, dy, bx - x, by - y)
                if angle < min_angle:
                    min_angle = angle
                    next_bubble = i
        return next_bubble

    results = []
    for dxi, dyi, Ki in finalists:
        max_angle = -float('inf')
        last_popped_bubble = None
        for i in range(n):
            x, y = bubbles[i]
            angle = angle_between(dxi, dyi, x, y)
            if angle > max_angle:
                max_angle = angle
                last_popped_bubble = i
        results.append(last_popped_bubble + 1)  # 1-indexed
    return results

