def count_good_colorings(k: int, n: int, special_nodes: List[Tuple[int, str]]) -> int:
    # Define the valid color pairs for each node
    valid_colors = {
        'white': {'green', 'red', 'orange'},
        'green': {'white', 'blue', 'yellow'},
        'red': {'white', 'orange', 'yellow'},
        'blue': {'green', 'orange', 'yellow'},
        'orange': {'red', 'blue', 'yellow'},
        'yellow': {'green', 'red', 'blue', 'orange'}
    }

    # Create a list to store the color of each node
    colors = [''] * (2**k)

    # Assign the special colors
    for v, s in special_nodes:
        colors[v - 1] = s

    # Function to check if the current coloring is valid
    def is_valid_coloring(node: int, color: str) -> bool:
        if node >= 2**k:
            return True
        if colors[node] and colors[node] != color:
            return False
        parent = (node - 1) // 2
        if parent >= 0:
            return is_valid_coloring(parent, color)
        return True

    # Function to count the number of valid colorings
    def count_colorings(node: int) -> int:
        if node >= 2**k:
            return 1
        if colors[node]:
            return count_colorings(node * 2 + 1) + count_colorings(node * 2 + 2)
        count = 0
        for color in valid_colors:
            if is_valid_coloring(node, color):
                colors[node] = color
                count = (count + count_colorings(node * 2 + 1) * count_colorings(node * 2 + 2)) % MOD
                colors[node] = ''
        return count

    return count_colorings(0)

