def simulate_balls(n: int, m: int, grid: List[List[int]], drops: List[int]) -> List[int]:
    result = []

    for drop_col in drops:
        current_row = 0
        current_col = drop_col - 1  # Convert to 0-based index

        while True:
            direction = grid[current_row][current_col]
            grid[current_row][current_col] = 2  # Change the cell direction to 2 (bottom)

            if direction == 1:
                if current_col + 1 < m:
                    current_col += 1
                else:
                    result.append(current_col + 1)  # Ball exits the grid at the rightmost column
                    break
            elif direction == 2:
                if current_row + 1 < n:
                    current_row += 1
                else:
                    result.append(current_col + 1)  # Ball exits the grid at the bottom row
                    break
            elif direction == 3:
                if current_col - 1 >= 0:
                    current_col -= 1
                else:
                    result.append(current_col + 1)  # Ball exits the grid at the leftmost column
                    break

    return result

