import sys

def find_enemy_base(grid_size: int) -> tuple[int, int, int, int]:
    # Define the boundaries of the grid
    min_x, max_x = 2, grid_size - 1
    min_y, max_y = 2, grid_size - 1

    # Function to check if a point is inside the enemy base
    def is_inside_base(x, y):
        if not (min_x <= x <= max_x and min_y <= y <= max_y):
            return True
        return False

    # Initialize the coordinates of the enemy base
    base_min_x, base_min_y, base_max_x, base_max_y = None, None, None, None

    # Perform binary search to find the enemy base
    while True:
        # Choose a random point within the grid
        x = (min_x + max_x) // 2
        y = (min_y + max_y) // 2

        # Query the device to get the Manhattan distance
        sys.stdout.write(f"? {x} {y}\n")
        sys.stdout.flush()

        distance = int(input())

        if distance == -1:
            # If the point is inside or outside the grid, adjust the boundaries
            if is_inside_base(x, y):
                if x < (min_x + max_x) // 2:
                    max_x = x - 1
                else:
                    min_x = x + 1
                if y < (min_y + max_y) // 2:
                    max_y = y - 1
                else:
                    min_y = y + 1
            else:
                # If the point is outside the grid, adjust the boundaries
                if x < (min_x + max_x) // 2:
                    max_x = x - 1
                else:
                    min_x = x + 1
                if y < (min_y + max_y) // 2:
                    max_y = y - 1
                else:
                    min_y = y + 1
        else:
            # If the point is inside the enemy base, adjust the boundaries
            if not is_inside_base(x, y):
                if x < (min_x + max_x) // 2:
                    max_x = x - 1
                else:
                    min_x = x + 1
                if y < (min_y + max_y) // 2:
                    max_y = y - 1
                else:
                    min_y = y + 1
            else:
                # If the point is inside the enemy base, update the base coordinates
                if base_min_x is None or x < base_min_x:
                    base_min_x = x
                if base_min_y is None or y < base_min_y:
                    base_min_y = y
                if base_max_x is None or x > base_max_x:
                    base_max_x = x
                if base_max_y is None or y > base_max_y:
                    base_max_y = y

    # Return the coordinates of the enemy base
    return base_min_x, base_min_y, base_max_x, base_max_y

