def count_pairs_to_delete(n: int, arr: List[int]) -> int:
    total_sum = sum(arr)
    k = total_sum / n

    if k * 2 == total_sum:
        return n * (n - 1) // 2

    count = 0
    element_count = {}
    for num in arr:
        element_count[num] = element_count.get(num, 0) + 1

    for num, freq in element_count.items():
        if freq > 1:
            count += freq * (freq - 1) // 2

    return count

