def can_obtain_ticks(n, m, k, field):
    # Create a copy of the field to mark painted cells
    painted = [[False] * m for _ in range(n)]

    # Function to check if a tick can be drawn at (i, j) with size d
    def can_draw_tick(i, j, d):
        if i - d < 0 or i + d >= n or j - d < 0 or j + d >= m:
            return False
        for h in range(d + 1):
            if field[i - h][j + h] == '.' or field[i + h][j + h] == '.' or \
               field[i - h][j - h] == '.' or field[i + h][j - h] == '.':
                return False
        return True

    # Function to mark the cells of a tick at (i, j) with size d
    def mark_tick(i, j, d):
        for h in range(d + 1):
            painted[i - h][j + h] = True
            painted[i + h][j + h] = True
            painted[i - h][j - h] = True
            painted[i + h][j - h] = True
            painted[i][j] = True

    # Check each cell in the field
    for i in range(n):
        for j in range(m):
            if field[i][j] == '*':
                # Try to draw ticks of all possible sizes at (i, j)
                for d in range(k, min(i, j, n - 1 - i, m - 1 - j) + 1):
                    if can_draw_tick(i, j, d):
                        mark_tick(i, j, d)

    # Verify if the painted field matches the input field
    for i in range(n):
        for j in range(m):
            if field[i][j] == '*' and not painted[i][j]:
                return False
            if field[i][j] == '.' and painted[i][j]:
                return False

    return True

