from typing import List, Tuple

def find_optimal_pair(n: int, chocolates: List[int]) -> Tuple[int, int, int]:
    # Create a dictionary to store the smallest power of 2 that can be used to make a pair
    power_dict = {}

    for i in range(n):
        # Find the smallest power of 2 that is greater than or equal to the current chocolate value
        power = 1
        while power <= chocolates[i]:
            power *= 2
        power //= 2

        # If this power has been seen before, we found a pair
        if power in power_dict:
            return (power_dict[power], i + 1, chocolates[i] - power)
        else:
            power_dict[chocolates[i]] = i + 1

    # If no pair is found, return a default value (though the problem guarantees a pair exists)
    return (-1, -1, -1)

