def find_perfect_sequences(n: int, M: int) -> int:
    def is_good_sequence(sequence: list) -> bool:
        max_val = max(sequence)
        min_val = min(sequence)
        sum_val = sum(sequence)
        return max_val * min_val >= sum_val

