    for _ in range(t):
        values = [-1] * (n + 1)
        dfs(1, -1, tree, values)

        # Check if the memories are consistent
        consistent = True
        for a, b, p in elves:
            xor_sum = 0
            while a != b:
                if values[a] != -1:
                    xor_sum ^= values[a]
                if values[b] != -1:
                    xor_sum ^= values[b]
                a //= 2
                b //= 2
            if p == 1 and bin(xor_sum).count('1') % 2 == 0:
                consistent = False
                break
            elif p == 0 and bin(xor_sum).count('1') % 2 != 0:
                consistent = False
                break

        if consistent:
            results.append("YES")
            for i in range(2, n + 1):
                results.append(f"{i} {i // 2} {values[i]}")
        else:
            results.append("NO")
    return results

