from typing import List

def solve_mosaic(n: int, grid: List[str]) -> str:
    def is_valid(x, y):
        return 0 <= x < n and 0 <= y < n

    def count_adjacent_tiles(x, y, color):
        count = 0
        directions = [(0, 1), (1, 0), (0, -1), (-1, 0)]
        for dx, dy in directions:
            nx, ny = x + dx, y + dy
            if is_valid(nx, ny) and grid[nx][ny] == color:
                count += 1
        return count

    def dfs(x, y, color):
        if not is_valid(x, y) or grid[x][y] != '.' or (x, y) in visited:
            return True
        visited.add((x, y))
        if grid[x][y] == '.':
            grid[x][y] = color
        if count_adjacent_tiles(x, y, color) != 2:
            return False
        return all(dfs(nx, ny, 'S' if color == 'G' else 'G') for nx, ny in [(x+1, y), (x-1, y), (x, y+1), (x, y-1)])

    def can_form_mosaic():
        for i in range(n):
            for j in range(n):
                if grid[i][j] == '.':
                    visited = set()
                    if not dfs(i, j, 'S'):
                        return False
        return True

    def print_mosaic():
        for row in grid:
            print(''.join(row))

    # Convert grid to a mutable list of lists
    grid = [list(row) for row in grid]

    if not can_form_mosaic():
        return "NONE"

    # Check for uniqueness
    original_grid = [row[:] for row in grid]
    for i in range(n):
        for j in range(n):
            if grid[i][j] == '.':
                grid[i][j] = 'S'
                if can_form_mosaic():
                    return "MULTIPLE"
                grid[i][j] = 'G'
                if can_form_mosaic():
                    return "MULTIPLE"
                grid[i][j] = '.'
                grid = [row[:] for row in original_grid]

    print_mosaic()
    return "UNIQUE"

