def generate_lexicographically_smallest_tree_string(n: int, k: int, labels: List[str], children: List[Tuple[int, int]]) -> str:
    # Helper function to perform an in-order traversal of the tree
    def in_order_traversal(node: int) -> str:
        if node == 0:
            return ""
        left_str = in_order_traversal(children[node - 1][0])
        right_str = in_order_traversal(children[node - 1][1])
        label = labels[node - 1]
        if left_str and right_str:
            return left_str + label + right_str
        elif left_str:
            return left_str + label
        elif right_str:
            return label + right_str
        else:
            return label

    # Helper function to check if we can duplicate the label of a node
    def can_duplicate_label(node: int, parent_duplicated: bool) -> bool:
        if node == 1:
            return True
        return parent_duplicated or labels[children[node - 2][0] - 1] != labels[node - 1]

    # Helper function to generate the lexicographically smallest tree string
    def generate_tree_string(node: int, k: int, parent_duplicated: bool) -> str:
        if node == 0:
            return ""
        if k > 0 and can_duplicate_label(node, parent_duplicated):
            original_label = labels[node - 1]
            labels[node - 1] = original_label + original_label
            k -= 1
            duplicated = True
        else:
            duplicated = False

        left_str = generate_tree_string(children[node - 1][0], k, duplicated)
        right_str = generate_tree_string(children[node - 1][1], k, duplicated)
        label = labels[node - 1]
        if left_str and right_str:
            result = left_str + label + right_str
        elif left_str:
            result = left_str + label
        elif right_str:
            result = label + right_str
        else:
            result = label

        if duplicated:
            labels[node - 1] = original_label

        return result

    return generate_tree_string(1, k, False)

