def can_erase_string(s: str) -> str:
    while True:
        original_length = len(s)
        s = s.replace('AB', '', 1)
        if len(s) == original_length:
            break
        original_length = len(s)
        s = s.replace('BC', '', 1)
        if len(s) == original_length:
            break
        if len(s) == 0:
            return 'YES'
    return 'NO'

