def find_max_triangle_area(w, h, horizontal_points, vertical_points):
    max_area = 0

    # Check horizontal sides
    for points in horizontal_points:
        max_area = max(max_area, max_triangle_area_from_points(points, w, h))

    # Check vertical sides
    for points in vertical_points:
        max_area = max(max_area, max_triangle_area_from_points(points, w, h))

    return max_area

def max_triangle_area_from_points(points, w, h):
    max_area = 0
    n = len(points)

    # Check all combinations of three points
    for i in range(n):
        for j in range(i + 1, n):
            for k in range(j + 1, n):
                area = triangle_area(points[i], points[j], points[k], w, h)
                max_area = max(max_area, area)

    return max_area

def triangle_area(p1, p2, p3, w, h):
    # Calculate the area of a triangle given three points and the dimensions of the rectangle
    # Use the Shoelace formula for lattice points
    x1, y1 = p1
    x2, y2 = p2
    x3, y3 = p3

    # Calculate the area using the Shoelace formula
    area = abs((x1 * (y2 - y3) + x2 * (y3 - y1) + x3 * (y1 - y2)) / 2.0)

    # Check if the triangle is valid
    if is_valid_triangle(p1, p2, p3, w, h):
        return area
    else:
        return 0

def is_valid_triangle(p1, p2, p3, w, h):
    # Check if the triangle is valid by ensuring no two points are the same and no point lies on the edge of the rectangle
    x1, y1 = p1
    x2, y2 = p2
    x3, y3 = p3

    # Check if any two points are the same
    if p1 == p2 or p1 == p3 or p2 == p3:
        return False

    # Check if any point lies on the edge of the rectangle
    if x1 == 0 or x1 == w or y1 == 0 or y1 == h:
        return False
    if x2 == 0 or x2 == w or y2 == 0 or y2 == h:
        return False
    if x3 == 0 or x3 == w or y3 == 0 or y3 == h:
        return False

    return True

