def is_square(n: int) -> bool:
    root = int(math.sqrt(n))
    return n == root * root

def find_quadratic_subset(n: int) -> Tuple[int, List[int]]:
    factorial_cache = [1] * (n + 1)
    for i in range(2, n + 1):
        factorial_cache[i] = factorial_cache[i - 1] * i

    max_subset_size = 0
    best_subset = []

    # Use a set to store the factorials to check for square products more efficiently
    factorial_set = set(factorial_cache)

    for subset_size in range(1, n + 1):
        for subset in range(1, 2**n):
            subset_elements = [i for i in range(1, n + 1) if (subset & (1 << (i - 1)))]
            if len(subset_elements) == subset_size:
                product = 1
                for element in subset_elements:
                    product *= factorial_cache[element]
                if is_square(product):
                    if subset_size > max_subset_size:
                        max_subset_size = subset_size
                        best_subset = subset_elements
    return max_subset_size, best_subset

