from typing import List

def find_nearly_full_subsequences(n: int, arr: List[int]) -> int:
    total_sum = sum(arr)
    if total_sum < 2:
        return 0
    target_sum = total_sum - 1
    count = 0

    # Helper function to count nearly full subsequences
    def count_subsequences(index, current_sum):
        nonlocal count
        if current_sum == target_sum:
            count += 1
        for i in range(index, n):
            if current_sum + arr[i] <= target_sum:
                count_subsequences(i + 1, current_sum + arr[i])

    # Iterate over all possible starting points
    for i in range(n):
        count_subsequences(i + 1, arr[i])

    return count

