    results = []
    for i in range(n + 1):
        operations = 0
        target_mex = i
        freq = [0] * (n + 1)

        for num in arr:
            freq[num] += 1

        j = 0
        while j <= n:
            if freq[j] == 0:
                if j == target_mex:
                    operations += 1
                    freq[j] += 1
                else:
                    break
            j += 1

        if j == n + 1:
            results.append(operations)
        else:
            results.append(-1)

    return results

