def can_force_to_lab(grid: List[List[str]], n: int, m: int) -> List[List[str]]:
    def is_valid(x: int, y: int) -> bool:
        return 0 <= x < n and 0 <= y < m and grid[x][y] != '#'

    def dfs(x: int, y: int):
        stack = [(x, y)]
        visited[x][y] = True
        force_cells.add((x, y))

        while stack:
            cx, cy = stack.pop()
            for dx, dy in directions:
                nx, ny = cx + dx, cy + dy
                if is_valid(nx, ny) and not visited[nx][ny]:
                    visited[nx][ny] = True
                    force_cells.add((nx, ny))
                    stack.append((nx, ny))

    directions = [(0, 1), (1, 0), (0, -1), (-1, 0)]
    visited = [[False] * m for _ in range(n)]
    force_cells = set()

    # Find the position of the lab
    lab_x, lab_y = -1, -1
    for i in range(n):
        for j in range(m):
            if grid[i][j] == 'L':
                lab_x, lab_y = i, j
                break
        if lab_x != -1:
            break

    # Perform DFS from the lab to mark all reachable force cells
    dfs(lab_x, lab_y)

    # Update the grid to mark force cells with '+'
    for i in range(n):
        for j in range(m):
            if (i, j) in force_cells:
                grid[i][j] = '+'

    return grid

