def is_composite(num: int) -> bool:
    if num <= 1:
        return False
    for i in range(2, int(num**0.5) + 1):
        if num % i == 0:
            return True
    return False

def find_largest_composite_subset(n: int, arr: List[int]) -> Tuple[int, List[int]]:
    max_subset_size = 0
    best_subset = []

    # Check all possible triplets in the array
    for i in range(n):
        for j in range(i + 1, n):
            for k in range(j + 1, n):
                subset_sum = arr[i] + arr[j] + arr[k]
                if is_composite(subset_sum):
                    subset_indices = [i + 1, j + 1, k + 1]
                    if len(subset_indices) > max_subset_size:
                        max_subset_size = len(subset_indices)
                        best_subset = subset_indices

    return (max_subset_size, best_subset)

