def find_permutation(n: int, a: int, b: int) -> Optional[List[int]]:
    if a + b >= n - 1:
        return None

    permutation = [0] * n
    peak_positions = [i for i in range(1, n-1) if i % 2 == 0]
    valley_positions = [i for i in range(1, n-1) if i % 2 == 1]

    if a > b:
        peak_positions, valley_positions = valley_positions, peak_positions

    peak_index = 0
    valley_index = 0
    for i in range(n):
        if i % 2 == 0:
            if peak_index < len(peak_positions):
                permutation[i] = peak_positions[peak_index] + 1
                peak_index += 1
            else:
                permutation[i] = valley_positions[valley_index] + 1
                valley_index += 1
        else:
            if valley_index < len(valley_positions):
                permutation[i] = valley_positions[valley_index] + 1
                valley_index += 1
            else:
                permutation[i] = peak_positions[peak_index] + 1
                peak_index += 1

    return permutation

