def find_bipartite_array(n: int, p: List[int]) -> Optional[List[int]]:
    # Create a list to store the bipartite array
    a = [0] * n

    # Create a list to store the color of each vertex
    color = [-1] * n

    # Function to perform BFS and check bipartiteness
    def bfs(start: int):
        queue = [start]
        color[start] = 0
        while queue:
            u = queue.pop(0)
            for v in range(n):
                if p[u] * p[v] < 0 and u < v:
                    if color[v] == -1:
                        color[v] = 1 - color[u]
                        queue.append(v)
                    elif color[v] == color[u]:
                        return False
        return True

    # Try to find a valid bipartite array starting from each vertex
    for start in range(n):
        if color[start] == -1:
            if not bfs(start):
                return None

    # Assign values to the bipartite array based on the color
    for i in range(n):
        a[i] = p[i] if color[i] == 0 else -p[i]

    return a

