def min_operations(start, end):
    n = len(start)
    # Initialize a DP table where dp[i][j] represents the minimum operations to match the first i characters of start with the first j characters of end
    dp = [[float('inf')] * (n + 1) for _ in range(n + 1)]

    # Base case: no operations needed to match empty strings
    dp[0][0] = 0

    # Fill the DP table
    for i in range(n + 1):
        for j in range(n + 1):
            if i > 0 and j > 0:
                # If both characters match or either is '?', no additional operation is needed
                if start[i - 1] == end[j - 1] or start[i - 1] == '?' or end[j - 1] == '?':
                    dp[i][j] = min(dp[i][j], dp[i - 1][j - 1])
                # If both characters are '?', we can either match or skip
                if start[i - 1] == '?' and end[j - 1] == '?':
                    dp[i][j] = min(dp[i][j], dp[i - 1][j - 1])

            if i > 0:
                # Skip a character in start
                dp[i][j] = min(dp[i][j], dp[i - 1][j] + 1)

            if j > 0:
                # Skip a character in end
                dp[i][j] = min(dp[i][j], dp[i][j - 1] + 1)

    return dp[n][n]

