def sort_array(n: int, arr: List[int]) -> Tuple[int, List[Tuple[int, int, int]]]:
    actions = []

    def apply_shift(l: int, r: int, d: int):
        segment = arr[l-1:r]
        shifted_segment = segment[d:] + segment[:d]
        arr[l-1:r] = shifted_segment
        actions.append((l, r, d))

    def find_segment_to_shift():
        for l in range(n):
            for r in range(l+1, n+1):
                for d in range(1, r-l+1):
                    segment = arr[l:r]
                    shifted_segment = segment[d:] + segment[:d]
                    if shifted_segment == sorted(arr[l:r]):
                        return l, r, d
        return None

    if arr == sorted(arr):
        return (0, [])

    while arr != sorted(arr):
        result = find_segment_to_shift()
        if result:
            l, r, d = result
            apply_shift(l, r, d)
        else:
            break

    return (len(actions), actions)

