def find_life(n: int, queries: List[List[int]]) -> int:
    # Initialize the permutation array with zeros
    permutation = [0] * n

    # Perform queries to gather information about the permutation
    for query in queries:
        s = [p + a for p, a in zip(permutation, query)]
        sum_count = {}
        for sum_value in s:
            if sum_value in sum_count:
                sum_count[sum_value] += 1
            else:
                sum_count[sum_value] = 1

        # Find the smallest index k where the sum occurs more than once
        min_index = n + 1
        for i, sum_value in enumerate(s):
            if sum_count[sum_value] > 1:
                min_index = min(min_index, i + 1)

        # If no such index k, return 0
        if min_index <= n:
            permutation[min_index - 1] = query[min_index - 1]

    return permutation

