def find_min_inversions(n: int, a: List[int]) -> int:
    deque = []
    inversions = 0
    left, right = 0, n - 1

    while left <= right:
        if not deque or a[left] <= a[right] <= deque[0]:
            deque.insert(0, a[right])
            right -= 1
        elif not deque or a[right] <= a[left] <= deque[-1]:
            deque.append(a[left])
            left += 1
        elif a[left] <= a[right]:
            deque.append(a[left])
            left += 1
        else:
            deque.insert(0, a[right])
            right -= 1

    for i in range(n):
        for j in range(i + 1, n):
            if deque[i] > deque[j]:
                inversions += 1

    return inversions

