def can_complete_level(n: int, grid: List[str]) -> str:
    # Check if the starting cell (1, 1) is safe
    if grid[0][0] == '1' or grid[1][n-1] == '1':
        return 'NO'

    # Traverse the grid
    for col in range(n):
        for row in range(2):
            if grid[row][col] == '1':
                # Mark the cell as unsafe
                continue
            if col > 0:
                # Propagate safety from the left
                grid[row] = '0' + grid[row][:col] + grid[row][col+1:]
            if col > 1:
                # Propagate safety from the left-left
                grid[row] = '0' + grid[row][:col-1] + grid[row][col:]
            if row == 0 and col > 0:
                # Propagate safety from the bottom-left
                grid[row+1] = '0' + grid[row+1][:col] + grid[row+1][col+1:]
            if row == 1 and col > 0:
                # Propagate safety from the top-left
                grid[row-1] = '0' + grid[row-1][:col] + grid[row-1][col+1:]

    # Check if the target cell (2, n) is reachable
    for col in range(n):
        if grid[0][col] == '1' or grid[1][col] == '1':
            return 'NO'

    return 'YES' if grid[1][n-1] == '0' else 'NO'

