# Function to calculate the modular exponentiation
def mod_exp(base, exp, mod):
    result = 1
    base = base % mod
    while exp > 0:
        if exp % 2 == 1:
            result = (result * base) % mod
        exp = exp >> 1
        base = (base * base) % mod
    return result

# Function to calculate the modular inverse
def mod_inv(a, mod):
    return mod_exp(a, mod - 2, mod)

# Precompute factorials and inverse factorials
def precompute_factorials(n, M):
    fact = [1] * (n + 1)
    inv_fact = [1] * (n + 1)
    for i in range(2, n + 1):
        fact[i] = (fact[i - 1] * i) % M
    inv_fact[n] = mod_inv(fact[n], M)
    for i in range(n - 1, 0, -1):
        inv_fact[i] = (inv_fact[i + 1] * (i + 1)) % M
    return fact, inv_fact

# Function to calculate nCr modulo M
def nCr(n, r, fact, inv_fact, mod):
    if r > n:
        return 0
    return (fact[n] * inv_fact[r] % mod) * inv_fact[n - r] % mod

# Main logic to count perfect sequences
def count_perfect_sequences(n, M):
    fact, inv_fact = precompute_factorials(n, M)
    result = 0
    for i in range(1, n + 2):
        for j in range(1, n + 2):
            if i * j >= i + j:
                remaining = n - 2
                if i == j:
                    remaining += 1
                result = (result + nCr(remaining, i - 1, fact, inv_fact, M) * nCr(remaining, j - 1, fact, inv_fact, M)) % M
    return result

