def find_max_steps(board: List[List[str]], n: int, m: int) -> Tuple[int, int, int]:
    def move(r, c, direction):
        if direction == 'L':
            return (r, max(c - 1, 0))
        elif direction == 'R':
            return (r, min(c + 1, m - 1))
        elif direction == 'D':
            return (max(r - 1, 0), c)
        elif direction == 'U':
            return (min(r + 1, n - 1), c)

