    start_position = (0, 0)

    for r in range(n):
        for c in range(m):
            visited = set()
            current_r, current_c = r, c
            steps = 0

            while (current_r, current_c) not in visited:
                visited.add((current_r, current_c))
                direction = board[current_r][current_c]
                current_r, current_c = move(current_r, current_c, direction)
                steps += 1

            if steps > max_steps:
                max_steps = steps
                start_position = (r + 1, c + 1)

    return start_position + (max_steps,)

