    def can_force_to_lab(grid, n, m, start_row, start_col):
        # Directions: right, down, left, up
        directions = [(0, 1), (1, 0), (0, -1), (-1, 0)]
        # Check if a cell is within the grid boundaries
        def is_valid(row, col):
            return 0 <= row < n and 0 <= col < m

        # Check if a cell can be forced to the lab
        def is_forced(row, col):
            for dr, dc in directions:
                new_row, new_col = row + dr, col + dc
                if is_valid(new_row, new_col) and grid[new_row][new_col] == 'L':
                    return True
            return False

        # BFS to determine if the robot can be forced to the lab from a given cell
        from collections import deque
        queue = deque([(start_row, start_col)])
        visited = [[False] * m for _ in range(n)]
        visited[start_row][start_col] = True

        while queue:
            row, col = queue.popleft()
            for dr, dc in directions:
                new_row, new_col = row + dr, col + dc
                if is_valid(new_row, new_col) and not visited[new_row][new_col]:
                    if grid[new_row][new_col] == '.':
                        if is_forced(new_row, new_col):
                            queue.append((new_row, new_col))
                            visited[new_row][new_col] = True
                        else:
                            return True
        return False

    results = []
    for grid in grids:
        n = len(grid)
        m = len(grid[0])
        result_grid = [['.' for _ in range(m)] for _ in range(n)]
        for i in range(n):
