import typing

def find_permutation(n: int, a: int, b: int) -> typing.Optional[list]:
    if a + b > n - 1:
        return None

    permutation = [0] * n
    peak_positions = list(range(1, n - 1))

    # Place the peaks
    for i in range(a):
        permutation[peak_positions.pop()] = i + 1

    # Place the valleys
    valley_positions = [i for i in range(n) if i not in peak_positions]
    for i in range(b):
        permutation[valley_positions.pop()] = a + i + 1

    # Fill the rest with increasing numbers
    remaining_positions = [i for i in range(n) if i not in permutation]
    for i in range(n):
        if permutation[i] == 0:
            permutation[i] = remaining_positions.pop(0)

    return permutation

