def subprocess_run_helper(
    func: callable,
    *args: Any,
    timeout: float = None,
    extra_env: Dict[str, str] = None
) -> subprocess.CompletedProcess:
    """Run a function in a subprocess with specified timeout and environment variables."""
    # Prepare command to run the function
    cmd = [
        sys.executable,
        '-c',
        f'import sys, os; from main import {func.__name__}; sys.exit({func.__name__}({", ".join(map(repr, args))}))'
    ]

    # Add extra environment variables if provided
    env = None
    if extra_env:
        env = dict(os.environ, **extra_env)

    # Run the command in a subprocess
    try:
        process = subprocess.run(
            cmd,
            stdout=subprocess.PIPE,
            stderr=subprocess.PIPE,
            timeout=timeout,
            check=True,
            env=env
        )
    except subprocess.CalledProcessError as e:
        raise RuntimeError(f"Subprocess failed with error: {e.stderr.decode()}")
    except subprocess.TimeoutExpired as e:
        raise RuntimeError(f"Subprocess timed out after {timeout} seconds")

    return process

