def _run_in_subprocess(conn: Any, func: callable, *args: Any, **kwargs: Any) -> None:
    """Run a function in a subprocess and send the result back through a connection."""
    try:
        result = func(*args, **kwargs)
        conn.send(result)
    except Exception as e:
        conn.send(e)
    finally:
        conn.close()

def subprocess_run_helper(func: callable, *args: Any, timeout: float, extra_env: Dict[str, str] = None) -> subprocess.CompletedProcess:
    """Run a function in a subprocess with timeout and environment variable handling."""
    parent_conn, child_conn = Pipe()
    p = Process(target=_run_in_subprocess, args=(child_conn, func, *args), env=extra_env if extra_env else os.environ)
    p.start()

    p.join(timeout)
    if p.is_alive():
        p.terminate()
        raise TimeoutError(f"Function {func.__name__} timed out after {timeout} seconds.")

    result = parent_conn.recv()
    if isinstance(result, Exception):
        raise result
    return subprocess.CompletedProcess(args=args, returncode=int(not result), stdout=str(result))

