    MOD = 10**9 + 7
    n = len(arr)

    # Precompute GCD values for indices
    index_gcds = precompute_gcds(n)

    result = 0
    for i in range(n):
        for j in range(n):
            result += arr[i] * arr[j] * index_gcds[i + 1][j + 1]
            result %= MOD

    return result

