import itertools

def find_successful_party(N: int, friendships: List[Tuple[int, int]]) -> Union[List[int], int]:
    """
    Finds a group of 5 people who are either all friends or all non-friends.

    Args:
        N (int): Number of people.
        friendships (List[Tuple[int, int]]): List of friendships.

    Returns:
        Union[List[int], int]: List of 5 people forming a successful party or -1 if impossible.
    """
    # Create an adjacency list to represent the graph
    graph = {i: set() for i in range(1, N + 1)}
    for u, v in friendships:
        graph[u].add(v)
        graph[v].add(u)

    # Function to check if a subset forms a clique
    def is_clique(subset):
        for i in range(len(subset)):
            for j in range(i + 1, len(subset)):
                if subset[j] not in graph[subset[i]]:
                    return False
        return True

    # Function to check if a subset forms an independent set
    def is_independent_set(subset):
        for i in range(len(subset)):
            for j in range(i + 1, len(subset)):
                if subset[j] in graph[subset[i]]:
                    return False
        return True

    # Check all combinations of 5 people
    for combination in itertools.combinations(range(1, N + 1), 5):
        if is_clique(combination) or is_independent_set(combination):
            return list(combination)
    return -1

