import os
import subprocess

def is_gitbash() -> bool:
    """
    Checks if the current program is run in a Windows gitbash terminal.

    Returns:
        bool: True if the current environment is gitbash, False otherwise.
    """
    try:
        result = subprocess.run(['which', 'git-bash'], stdout=subprocess.PIPE, stderr=subprocess.PIPE)
        if 'git-bash' in result.stdout.decode('utf-8'):
            return True
        else:
            return False
    except FileNotFoundError:
        return False

import subprocess
import sys
from typing import Callable, Tuple, Optional, Dict
from subprocess import CompletedProcess

def subprocess_run_helper(func: Callable, *args: Tuple, timeout: int, extra_env: Optional[Dict[str, str]] = None) -> CompletedProcess:
    """
    Run a function in a sub-process

    Args:
        func (callable): The function to be run. It must be in a module that is importable.
        *args (tuple): Any additional command line arguments to be passed in
                    the first argument to ``subprocess.run``.
        timeout (int): The maximum time (in seconds) to wait for the subprocess to complete.
        extra_env (dict, optional): Any additional environment variables to be set for the subprocess. Defaults to None.
    Returns:
        CompletedProcess: An instance of subprocess.CompletedProcess containing the return code and output.
    """
    if extra_env is None:
        extra_env = {}

    def run_function_in_subprocess():
        """Helper function to run the provided function in a subprocess."""
        try:
            result = func(*args)
            return result
        except Exception as e:
            return e

    env = {**sys.env, **extra_env}

    try:
        with subprocess.Popen(
            [sys.executable, '-c', 'import run_function_in_subprocess; result = run_function_in_subprocess.run_function_in_subprocess()'],
            stdout=subprocess.PIPE,
            stderr=subprocess.PIPE,
            env=env
        ) as process:
            try:
                stdout, stderr = process.communicate(timeout=timeout)
                return_code = process.returncode
                if return_code is None:
                    process.kill()
                    stdout, stderr = process.communicate()
                return CompletedProcess(process.args, return_code, stdout, stderr)
            except subprocess.TimeoutExpired:
                process.kill()
                stdout, stderr = process.communicate()
                return CompletedProcess(process.args, -1, stdout, stderr)
    except Exception as e:
        return CompletedProcess([], -1, b'', str(e).encode())

