def round_half_to_even(n: float) -> float:
    """
    Round a floating-point number to the nearest even number when it is exactly halfway between two integers.
    """
    if n % 1 == 0.5:
        return round(n - 0.1) if n % 2 == 0 else round(n + 0.1)
    return round(n)

