        raise ValueError("Each character in the alphabet must be a string")

    if not alphabet:
        raise ValueError("Alphabet must be a non-empty list")

    if padding is not None and not isinstance(padding, int):
        raise ValueError("Padding must be an integer or None")

    if padding is not None and padding < 0:
        raise ValueError("Padding must be non-negative")

    if len(set(alphabet)) != len(alphabet):
        raise ValueError("Alphabet must contain unique characters")

