def round_half_to_even(n: float) -> float:
    """
    Rounds a floating-point number according to the half-to-even rounding mode.

    Args:
        n (float): The number to be rounded.

    Returns:
        float: The rounded number.
    """
    if not isinstance(n, (int, float)):
        raise ValueError("Input must be a number")

    if not n == n:  # Check for NaN
        return float('nan')

    if n == float('inf') or n == float('-inf'):  # Check for Infinity
        return float('inf') if n > 0 else float('-inf')

    # Convert to integer to apply rounding
    abs_n = abs(int(n))
    fraction = abs(n) - abs_n

    if fraction < 0.5:
        return round(n)
    elif fraction > 0.5:
        return -(round(-n)) if n < 0 else round(n)
    else:  # fraction == 0.5
        return int(n) + (0 if int(n) % 2 == 0 else 1)

