def int_to_string(number: int, alphabet: List[str], padding: Optional[int] = None) -> str:
    """
    Convert a number to a string, using the given alphabet.
    The number represents a short uuid.
    The output has the most significant digit first.
    @param number: Int value
    @param alphabet : List with letters
    @param padding : Optional with int value, default is None
    @return  string value corresponded to int
    """
    if not alphabet:
        raise ValueError("Alphabet must be provided and not empty.")

    if number < 0:
        raise ValueError("Number must be non-negative.")

    # Handle the case where the number is 0
    if number == 0:
        return alphabet[0] if padding is None or padding >= 1 else '0' * padding

    # Convert the number to a string using the given alphabet
    base = len(alphabet)
    result = []
    while number > 0:
        number, remainder = divmod(number, base)
        result.append(alphabet[remainder])

    result_str = ''.join(reversed(result))

    # Apply padding if needed
    if padding is not None:
        if padding < len(result_str):
            raise ValueError("Padding is too small for the number.")
        result_str = result_str.zfill(padding)

    return result_str

